import React, { useEffect, useState } from "react";
import { Breadcrumb, SimpleCard } from "@gull";
import { connect } from 'react-redux';
import { useDispatch, useSelector } from 'react-redux';
import { getOrderView } from "app/redux/actions/OrderActions";
import { uploadVendorList, getInwardList } from "app/redux/actions/InventoryActions";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import ToolkitProvider from "react-bootstrap-table2-toolkit";
import { Route, Link, BrowserRouter, withRouter, useHistory } from "react-router-dom";
import { browserHistory } from 'react-router';
import {
    Modal,
    Form,
    FormLabel,
    FormGroup,
    FormControl,
    Dropdown,
    Button,
    Badge
} from 'react-bootstrap';
import axios from 'axios';
import * as common from "app/services/common";
import { NotificationContainer, NotificationManager } from "react-notifications";
import { apiUrl, rootPath } from 'app/config';

import stockImport from './stock_import.xlsx';

const downloadIcon = 'data:image/png;base64,';


const MyItemComp = (props) => {

    const dispatch = useDispatch()
    const history = useHistory();

    useEffect(() => {
        props.getOrderView(defaultParams);
        props.getInwardList();
    }, [])

    const defaultParams = {
        "member_id": props.match.params.memberId,
        "order_id": props.match.params.orderId
    }

    const [smShow, setSmShow] = useState(false);
    const [pristine, setPristine] = useState(false);
    const [customerData, setCustomerData] = useState([]);
    const [showModal, setShowModal] = useState(false);

    let sortableColumn = [
        {
            text: "S.No",
            formatter: (cell, row, index) => {
                return <>{index + 1}</>;
            },
            headerStyle: {
                width: '5%',
                textAlign: 'center',
            },
            classes: 'text-center',
            sort: false
        },
        {
            headerStyle: {
                textAlign: 'center',
            },
            dataField: "pur_date",
            text: "Inward Date",
            sort: false
        },
        {
            headerStyle: {
                textAlign: 'center',
            },
            dataField: "pur_order_no",
            text: "Inward No.",
            sort: false
        },
        {
            headerStyle: {
                textAlign: 'center',
            },
            dataField: "vendor_name",
            text: "Vendor Name",
            sort: false
        },
        {
            headerStyle: {
                textAlign: 'center',
            },
            dataField: "totalqty",
            text: "Qty",
            sort: false
        },
        {
            headerStyle: {
                textAlign: 'center',
            },
            dataField: "net_amount",
            text: "Amount",
            sort: false
        },
        {
            headerStyle: {
                textAlign: 'center',
            },
            dataField: "status",
            text: "Status",
            formatter: (cell, row, index) => {
                return (row.status === 1) ? <Badge className="p-2 m-1 badge badge-success">Active</Badge> : <Badge className="p-2 m-1 badge badge-danger">In Active</Badge>;
            },
            sort: false
        },
        {
            text: "Action",
            headerStyle: {
                textAlign: 'center',
            },
            style: {
                whiteSpace: 'nowrap',
            },
            formatter: (cell, row, index) => {
                return <>
                    <Button
                        className='m-1'
                        variant='outline-primary'
                        onClick={() => {
                            setShowModal(true);
                            setCustomerData([row]);
                        }}>
                            <i className='text-18 ion-ios-eye'></i>
                    </Button>
                </>;
            },
            classes: 'text-center',
            sort: false
        },
    ];


    let paginationOptions = {
        custom: false,
        paginationSize: 5,
        pageStartIndex: 1,
        firstPageText: "First",
        prePageText: "Back",
        nextPageText: "Next",
        lastPageText: "Last",
        nextPageTitle: "First page",
        prePageTitle: "Pre page",
        firstPageTitle: "Next page",
        lastPageTitle: "Last page",
        showTotal: true,
        totalSize: props.inwardList && props.inwardList.length || 0
    };
    const handleVendorUpload = () => {
        const files = document.getElementById('vendorFile') && document.getElementById('vendorFile').files;
        if (files && files.length > 0 && files[0]) {
            const fileName = files[0].name;
            const extension = fileName && fileName.substring(fileName.lastIndexOf('.') + 1).toLowerCase() || '';
            if (extension !== 'xls' && extension !== 'xlsx') {
                NotificationManager.warning(
                    'Kindly Upload Only .xls files.'
                );
                return false;
            }

            setPristine(true);
            const fd = new FormData();
            fd.append('file', files[0]);
            axios
                .post(apiUrl + "uploadfile", fd, { headers: { 'Content-Type': 'multipart/form-data' } })
                .then(res => {
                    console.log(res);
                    setSmShow(false);
                    NotificationManager.success('Data Imported Successfully');
                })
                .catch((err) => {
                    NotificationManager.warning(
                        err.message
                    );
                    console.log(err);
                }).finally(() => {
                    setPristine(false);
                })
                
        } else {
            NotificationManager.warning(
                'Please upload the file'
            );
        }

    }

    return (
        <>
            <Breadcrumb
                routeSegments={[
                    { name: "Inventory", path: rootPath + "inventory/manage-inward" },
                    { name: "Manage Inward" }
                ]}
            />
            <SimpleCard title="Inward List">
                <div className="text-right">
                      <Button
                        onClick={() => setSmShow(true)}
                        variant="outline-success m-1 text-capitalize">
                        Import Inward
                    </Button>
                    <Modal
                        size="md"
                        show={smShow}
                        onHide={() => setSmShow(false)}
                        aria-labelledby="example-modal-sizes-title-md">
                        <Modal.Header closeButton>
                            <Modal.Title id="example-modal-sizes-title-md">
                                <h3>Upload Excel</h3>
                            </Modal.Title>
                        </Modal.Header>
                        <Modal.Body>
                            <h4>Instructions!</h4>
                            <ul className="pl-5">
                                <li>Kindly Upload Only .xls files.</li>
                                <li>The sheet consists of Product Name, Product Type, etc. Fields marked are to be entered mandatory.</li>
                                <li>Download the Sample sheet attached for your reference. <span onClick={() => window.open(stockImport, '_self')}><img src={downloadIcon} alt="download-icon" style={{ width: '30px', cursor: 'pointer' }} /></span></li>
                                <li>Add the entries to the sheet.save it and upload the sheet.</li>
                                <li>In case of any queries or issues, please contact.</li>
                            </ul>
                            <div>
                                <input type="file" className="w-100 border border-secondary" accept=".xlsx, .xls, .csv" id="vendorFile" name="filename" />
                            </div>
                        </Modal.Body>
                        <Modal.Footer className="justify-content-center">
                            <FormGroup className="d-flex align-items-center justify-content-center">
                                <Button className="w-100" variant="outline-primary m-1 text-capitalize" onClick={handleVendorUpload} disabled={pristine}>{pristine ? 'Uploading...' : 'Upload'}</Button>
                                <Button className="w-100" variant="outline-danger m-1 text-capitalize" onClick={() => setSmShow(false)}>Cancel</Button>
                            </FormGroup>
                        </Modal.Footer>
                    </Modal>
                </div>
                <div className="mx-3 mt-3">
                    <ToolkitProvider
                        striped
                        keyField='_id'
                        data={props.inwardList || []}
                        columns={sortableColumn}>
                        {props => (
                            <>
                                <BootstrapTable
                                    {...props.baseProps}
                                    bootstrap4
                                    className="table-responsive"
                                    keyField='_id'
                                    pagination={paginationFactory(paginationOptions)}
                                    headerClasses="datatable-header"
                                    noDataIndication={"Inward List is empty"}

                                />
                            </>
                        )}
                    </ToolkitProvider>
                </div>
            </SimpleCard>
            <NotificationContainer />
            <Modal show={showModal} onHide={() => setShowModal(false)} backdrop="static" keyboard={false} size="lg">
                <Form method="POST" onSubmit={event => {
                    event.preventDefault();
                    setShowModal(false);
                }}>
                    <Modal.Header closeButton>
                        <Modal.Title>View Customer</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <Form.Row>
                            <FormGroup className="col-md-4 mb-3 pl-0">
                                <FormLabel>Inward Date</FormLabel>
                                <FormControl
                                    type="text"
                                    disabled={true}
                                    className="form-control col-md-12"
                                    name="pur_date"
                                    value={(customerData.length > 0) ? customerData[0].pur_date : ''}
                                />
                                <FormControl
                                    type="hidden"
                                    name='_id'
                                    value={(customerData.length > 0) ? customerData[0]._id : ''}
                                />
                            </FormGroup>
                            <FormGroup className="col-md-4 mb-3 pl-0">
                                <FormLabel>Inward No.</FormLabel>
                                <FormControl
                                    type="text"
                                    className="form-control col-md-12"
                                    name='pur_order_no'
                                    disabled={true}
                                    value={(customerData.length > 0) ? customerData[0].pur_order_no : ''}
                                />
                            </FormGroup>
                            <FormGroup className="col-md-4 mb-3 pl-0">
                                <FormLabel>Vendor Name</FormLabel>
                                <FormControl
                                    type="text"
                                    disabled={true}
                                    className="form-control col-md-12"
                                    name='vendor_name'
                                    value={(customerData.length > 0) ? customerData[0].vendor_name : ''}
                                />
                            </FormGroup>
                            <FormGroup className="col-md-4 mb-3 pl-0">
                                <FormLabel>Qty</FormLabel>
                                <FormControl
                                    type="text"
                                    disabled={true}
                                    className="form-control col-md-12"
                                    name='totalqty'
                                    value={(customerData.length > 0) ? customerData[0].totalqty : ''}
                                />
                            </FormGroup>
                            <FormGroup className="col-md-4 mb-3 pl-0">
                                <FormLabel>Amount</FormLabel>
                                <FormControl
                                    type="text"
                                    disabled={true}
                                    className="form-control col-md-12"
                                    name='net_amount'
                                    value={(customerData.length > 0) ? customerData[0].net_amount : ''}
                                />
                            </FormGroup>
                            <FormGroup className="col-md-4 mb-3 pl-0">
                                <FormLabel>Status</FormLabel>
                                <FormControl
                                    as="select"
                                    className="form-control col-md-12"
                                    name='status'
                                    disabled>
                                    <option value="" hidden>Select Status</option>
                                    <option selected={(customerData.length > 0 ? (customerData[0].status === 5 ? true : false) : false)} value="5">Rejected</option>
                                    <option selected={(customerData.length > 0 ? (customerData[0].status === 6 ? true : false) : false)} value="6">Hold</option>
                                    <option selected={(customerData.length > 0 ? (customerData[0].status === 1 ? true : false) : false)} value="1">Verified</option>
                                    <option selected={(customerData.length > 0 ? (customerData[0].status === 0 ? true : false) : false)} value="0">Block</option>
                                    <option selected={(customerData.length > 0 ? (customerData[0].status === 2 ? true : false) : false)} value="2">Inactive</option>
                                </FormControl>
                                <FormControl
                                    type="hidden"
                                    name='customer_id'
                                    value={(customerData.length > 0) ? customerData[0]._id : 0}
                                />
                            </FormGroup>
                        </Form.Row>
                    </Modal.Body>
                    <Modal.Footer>
                        <div className="float-right">
                            <Button variant="outline-danger m-1 text-capitalize" onClick={() => setShowModal(false)}>Cancel</Button>
                            <Button type="submit" variant="outline-primary m-1 text-capitalize" >Submit</Button>
                        </div>
                    </Modal.Footer>
                </Form>
            </Modal>
        </>
    )
}

function mapStateToProps(state) {
    return {
        orderView: state.order.orderView,
        inwardList: state.inventory.inwardList
    };
}

function mapDispatchToProps(dispatch) {
    return {
        getOrderView: e => dispatch(getOrderView(e)),
        getInwardList: e => dispatch(getInwardList(e))
    };
}

const ManageInward = connect(mapStateToProps, mapDispatchToProps)(MyItemComp)

export default ManageInward;